var chalk = require('chalk');

module.exports = (resolved, rejected) => promise => promise.then(
  val => {console.log(chalk.green('✔ ') + (typeof resolved === 'function' ? resolved(val) : resolved)); return val},
  err => {console.error(chalk.red('✗ ') + (typeof rejected === 'function' ? rejected(err) : rejected)); throw  err}
);